package com.core.java.ObjectClass.string;

public class SubString {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String  s = "newspaper";
		String s1 = s.substring(5, 7);
		
		s1 = s1 + 'a';
		
		System.out.println("String value is : "+ s1);

	}

}
